import React, { Component } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import TableRow from '@material-ui/core/TableRow'
import Card from '@material-ui/core/Card'
import CardContent from '@material-ui/core/CardContent'
import CardMedia from '@material-ui/core/CardMedia'
import IconButton from '@material-ui/core/IconButton'
import Typography from '@material-ui/core/Typography'
import Grid from '@material-ui/core/Grid'
import axios from 'axios'
import ReactDOM from 'react-dom'

async function foundData () {
  var mangaArray = []
  await axios.get('/api/Manga')
    .then((result) => {
      result.map(manga => {
        var mangaNode = [manga._id, manga.img, manga.title, manga.author, manga.foloNumber, manga.creatDate]
        this.setState({ manga: mangaNode })
      })
    })
    .catch((error) => {
      var mangaNode = createData(0, 'https://source.unsplash.com/random', 'QQQ', 'Elvis Presley', 60, '16 Mar, 2019')
      mangaArray.push(mangaNode)
      this.setState({ mangas: mangaArray })
      console.log(mangaArray)
      console.log(mangaNode)
      if (error.response.status === 401) {
        console.log('null manga')
      }
    })
  return (
    <div><Orders props={mangaArray} /></div>)
}

class ImgLinks extends Component {
  constructor (props) {
    super(props)
    this.state = { name: '张珊珊' }
  }

  render () {
    return (
      <ImgLink name={this.name} />
    )
  }
}

function createData (id, img, title, auteur, numofcom, date) {
  return { id, img, title, auteur, numofcom, date }
}

const useStyles = makeStyles(theme => ({
  card: {
    display: 'flex'
    // margin: 30
  },
  details: {
    display: 'flex',
    flexDirection: 'column'
  },
  content: {
    flex: '1 0 auto'
  },
  cover: {
    width: 300
  },
  playIcon: {
    height: 38,
    width: 38
  },
  items: {
    marginTop: 30
  }
}))

export default function Orders (props) {
  const classes = useStyles()
  console.log(props)
  return (
    <>
      <Grid container spacing={1} className={classes.items}>
        {

          props.props.map(row => (
            <TableRow key={row.id}>
              <Grid item md={12} container spacing={2}>
                <Card className={classes.card}>
                  <CardMedia
                    className={classes.cover}
                    image={row.img}
                  />
                  <div className={classes.details}>
                    <CardContent className={classes.content}>
                      <Typography component='h5' variant='h5'>{row.title}</Typography>
                      <Typography variant='subtitle1' color='textSecondary'>{row.auteur}</Typography>
                      <Typography variant='subtitle1' color='textSecondary'>{row.date}</Typography>
                      <div>
                        <Typography variant='subtitle1' color='textSecondary'>{row.auteur}</Typography>
                        <Typography variant='subtitle1' color='textSecondary'>{row.numofcom}</Typography>
                      </div>

                    </CardContent>
                  </div>
                </Card>
              </Grid>
            </TableRow>
          ))

        }
      </Grid>
    </>
  )
}
