import React, { Component } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import TableRow from '@material-ui/core/TableRow'
import Card from '@material-ui/core/Card'
import CardContent from '@material-ui/core/CardContent'
import CardMedia from '@material-ui/core/CardMedia'
import IconButton from '@material-ui/core/IconButton'
import Typography from '@material-ui/core/Typography'
import Grid from '@material-ui/core/Grid'
import axios from 'axios'

class Page extends Component {
  constructor () {
    super()
    this.state = {
      mangas: [],
      message: null
    }
  }

  foundData () {
    var mangaArray = []
    axios.get('/api/Manga')
      .then((result) => {
        result.map(manga => {
          var mangaNode = [manga._id, manga.img, manga.title, manga.author, manga.foloNumber, manga.creatDate]
          this.setState({ manga: mangaNode })
        })
      })
      .catch((error) => {
        var mangaNode = createData(0, 'https://source.unsplash.com/random', 'QQQ', 'Elvis Presley', 60, '16 Mar, 2019')
        mangaArray.push(mangaNode)
        this.setState({ mangas: mangaArray })
        console.log(mangaArray)
        console.log(mangaNode)
        console.log(this.state)
        if (error.response.status === 401) {
          console.log('null manga')
        }
      })
    console.log(this.state)
    return mangaArray
  }

  render () {
    return Orders()
  }
}
// Generate Order Data
var page1 = new Page()
page1.foundData()

function createData (id, img, title, auteur, numofcom, date) {
  return { id, img, title, auteur, numofcom, date }
}

const useStyles = makeStyles(theme => ({
  card: {
    display: 'flex'
    // margin: 30
  },
  details: {
    display: 'flex',
    flexDirection: 'column'
  },
  content: {
    flex: '1 0 auto'
  },
  cover: {
    width: 300
  },
  playIcon: {
    height: 38,
    width: 38
  },
  items: {
    marginTop: 30
  }
}))

